class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        if sum(nums) == 0:
            return 0

        n = len(nums)
        finish = [False] * n

        buf = [set([]) for _ in range(n)]

        ans = 1
        for i, j, k in queries:
            for m in range(i, j+1):
                if finish[m]:
                    continue
                newset = set([])
                newset.add(k)
                for x in buf[m]:
                    newset.add(x+k)
                    newset.add(x)
                buf[m] = newset

            f = True
            for m in range(n):
                if finish[m]:
                    continue
                if nums[m] in buf[m] or nums[m] == 0:
                    finish[m] = True
                else:
                    f = False
            if f:
                return ans
            ans += 1
        return -1

