class Solution {
    public class Node {
        public Set<Integer> set = new HashSet<>();
        public Map<Integer, Integer> map = new HashMap<>();
    }

    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        Node[] arr = new Node[n];
        Arrays.setAll(arr, i -> new Node());
        for (int k = 0; k < queries.length; k++) {
            int[] query = queries[k];
            for (int x = query[0]; x <= query[1]; x++) {
                Node node = arr[x];
                int val = query[2];
                Map<Integer, Integer> temp = new HashMap<>();
                for (int e : node.set) {
                    if (val + e > 1000) {
                        continue;
                    }
                    if (!node.set.contains(val + e)) {
                        temp.put(val + e, k);
                    }
                }
                node.set.addAll(temp.keySet());
                node.map.putAll(temp);
                if (!node.set.contains(val)) {
                    node.set.add(val);
                    node.map.put(val, k);
                }
            }
        }
        int max = 0;
        boolean flag=false;
        for (int i = 0; i < n; i++) {
            if (0 == nums[i]) {
                continue;
            }
            flag=true;
            if (!arr[i].set.contains(nums[i])) {
                return -1;
            }
            max = Math.max(max, arr[i].map.get(nums[i]));
        }
        return flag?max + 1:0;

    }
}

