class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        vector<vector<int>> dp(n, vector<int>{});

        for (int i = 0; i < n; ++i) {
            dp[i] = vector<int>(nums[i] + 1, 0);
            dp[i][nums[i]] = 1;
        }

        int r = 0, now = 0;
        while (true) {
            bool ok = true;
            for (int i = 0; i < n; ++i) {
                if (dp[i][0] == 0) {
                    ok = false;
                    break;
                }
            }

            if (ok) {
                return now;
            }

            if (now >= queries.size())
                return -1;

            for (int d = queries[now][0]; d <= queries[now][1]; ++d) {
                for (int p = 0; p < dp[d].size(); ++p) {
                    if (dp[d][p] && p - queries[now][2] >= 0)
                        dp[d][p - queries[now][2]] = 1;
                }
            }
            ++now;
        }

        return -1;
    }
};

