class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();
        vector<vector<pair<int, int>>> cover(n);
        for (int k = 0; k < m; ++k) {
            int l = queries[k][0];
            int r = queries[k][1];
            int val = queries[k][2];
            if (l > r)
                continue;
            for (int i = l; i <= r; ++i) {
                if (i < n) {
                    cover[i].emplace_back(k, val);
                }
            }
        }

        int max_k = -1;
        for (int i = 0; i < n; ++i) {
            int target = nums[i];
            if (target == 0) {
                continue;
            }

            vector<pair<int, int>>& qs = cover[i];
            unordered_set<int> possible_sums;
            possible_sums.insert(0);
            bool found = false;
            int current_max_k = -1;

            for (const auto& q : qs) {
                int k_index = q.first;
                int val = q.second;

                unordered_set<int> new_sums;
                for (int s : possible_sums) {
                    int new_sum = s + val;
                    if (new_sum == target) {
                        current_max_k = k_index;
                        found = true;
                        break;
                    }
                    new_sums.insert(new_sum);
                }
                if (found)
                    break;

                possible_sums.insert(new_sums.begin(), new_sums.end());
            }

            if (!found) {
                return -1;
            }

            if (current_max_k > max_k) {
                max_k = current_max_k;
            }
        }
        return max_k + 1;
    }
}
;

