class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        vector<vector<pair<int,int>>> spot(nums.size());
        for(int i = 0;i<queries.size();i++){
            int l = queries[i][0], r = queries[i][1], val = queries[i][2];
            for(int j = l;j<=r;j++){
                spot[j].emplace_back(val,i);
            }
        }
        int allready = true;
        for(auto it:nums)
            if(it>0){
                allready = false;
                break;
            }
        if(allready) return 0;
        int l = 0, r = queries.size()-1;
        auto check = [&](int k)->bool{
            vector<unordered_set<int>> sub(nums.size());
            //cout<<"k is "<<k<<endl;
            for(int i = 0;i<nums.size();i++){
                if(nums[i] == 0) continue;
                int target = nums[i], j = 0;
                bool find = false;
                sub[i].emplace(0);
                //cout<<target<<endl;
                while(j<spot[i].size() && spot[i][j].second<=k){
                    //cout<<j<<endl;
                    int cur = spot[i][j].first;
                    //cout<<"cur is"<<cur<<endl;
                    if(cur <= target){
                        if(sub[i].count(target - cur)){
                            //cout<<"find!"<<cur<<" "<<target-cur<<endl;
                            find = true;
                            break;
                        }
                        else{
                            vector<int> tmp;
                            for(auto it:sub[i]){
                                if(cur + it <target)
                                    tmp.emplace_back(cur + it);
                            }
                            for(auto it:tmp)
                                sub[i].emplace(it);
                        }
                    }
                    j++;
                }
                //cout<<i<<" "<<j<<" "<<find<<endl;
                if(find == false) return false;
            }
            return true;
        };
        while(l<=r){
            int mid = l + (r-l)/2;
            if(check(mid)) r = mid-1;
            else l = mid+1;
        }
        return l == queries.size()?-1:l+1;
    }
};

