class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
int n = nums.size();
        int m = queries.size();
        vector<vector<pair<int, int>>> cover(n);

        for (int j = 0; j < m; ++j) {
            int l = queries[j][0];
            int r = queries[j][1];
            int val = queries[j][2];
            if (l < n) cover[l].emplace_back(val, j);
            if (r + 1 < n) cover[r + 1].emplace_back(-val, j);
        }
        vector<vector<pair<int, int>>> elementCover(n);
        vector<int> currentQueries;
        for (int i = 0; i < n; ++i) {
            for (auto &p : cover[i]) {
                if (p.first > 0) {
                    currentQueries.push_back(p.second);
                } else {
                    auto it = find(currentQueries.begin(), currentQueries.end(), p.second);
                    if (it != currentQueries.end()) currentQueries.erase(it);
                }
            }
            for (int j : currentQueries) {
                int l = queries[j][0];
                int r_val = queries[j][2];
                if (i >= l && i <= queries[j][1]) {
                    elementCover[i].emplace_back(r_val, j);
                }
            }
        }
        for (int i = 0; i < n; ++i) {
            sort(elementCover[i].begin(), elementCover[i].end(), [](const pair<int, int>& a, const pair<int, int>& b) {
                return a.second < b.second;
            });
        }        
        int max_k = -1;
        for (int i = 0; i < n; ++i) {
            int target = nums[i];
            if (target == 0) continue;
            vector<int> dp(target + 1, -1);
            dp[0] = -1;
            for (auto& p : elementCover[i]) {
                int val = p.first;
                int j = p.second;
                for (int s = target; s >= val; --s) {
                    if (dp[s - val] != -1) {
                        int current_max = max(j, dp[s - val]);
                        if (dp[s] == -1 || current_max < dp[s]) {
                            dp[s] = current_max;
                        }
                    }
                }
                if (val <= target && (dp[val] == -1 || j < dp[val])) {
                    dp[val] = j;
                }
                if (dp[target] != -1) break;
            }
            if (dp[target] == -1) return -1;
            max_k = max(max_k, dp[target]);
        }
        return max_k == -1 ? 0 : max_k + 1;
    }
};

