class Solution {
public:
  int minZeroArray(vector<int> &nums, vector<vector<int>> &queries) {
    /**
     * numsqueries, -valnums0
     * queryLq, Rq
     * dp[i][j]ij
     * for each queries:
     *  dp[i][j] = min(dp[i][j], dp[i][j+val] + 1)
     * max(dp[i][0]) -1
     * : INT_MAX, dp[i][nums[i]] = 0
     */
    int nums_max = 0;
    const int kQueryMax = 10000;
    vector<vector<int>> dp(nums.size(), vector<int>(1001, kQueryMax));
    for (int i = 0; i < nums.size(); ++ i) {
      dp[i][nums[i]] = 0;
      nums_max = max(nums_max, nums[i]);
    }
    for (int k = 0; k < queries.size(); ++ k) {
      auto& query = queries[k];
      int left = query[0], right = query[1], val = query[2];
      for (int i = 0; i < nums.size(); ++ i) {
        if (left > i || right < i) continue;
        for (int j = 0; j <= nums_max; ++ j) {
          if (j + val <= nums_max && dp[i][j + val] < kQueryMax) {
            dp[i][j] = min(dp[i][j], k + 1);
          }
        }
      }
    }
    int ans = 0;
    for (int i = 0; i < nums.size(); ++ i) {
      ans = max(ans, dp[i][0]);
    }
    return ans == kQueryMax ? -1 : ans;
  }
};

