class Solution {
public:
    int minZeroArray(const vector<int>& nums,const vector<vector<int>>& queries) {
        int n = queries.size();
        int id = 0;
        vector memo(n, vector(1001, -1));
        function<int(int,int)> dfs = [&](int index,int remain)->int{
            if (remain == 0)return index;
            if (index == n)return INT_MAX;
            if (memo[index][remain] != -1)return memo[index][remain];
            int ans = INT_MAX;
            if (id <= queries[index][1] && id >= queries[index][0]) {
                ans = min({ ans, remain - queries[index][2] >= 0 ? dfs(index + 1, remain - queries[index][2]) : INT_MAX, dfs(index + 1, remain) });
            }
            else {
                ans = min(ans, dfs(index + 1, remain));
            }
            return memo[index][remain]=ans;
        };
        int ans = 0;
        for (int i = 0; i < nums.size(); i++) {
            memo = vector(n, vector(1001, -1));
            id = i;
            int id=dfs(0, nums[i]);
            if (id == -1)return -1;
            else {
                ans = max(ans, id);
            }
        }
        return ans > queries.size() ? -1 : ans;
    }
};

