func minZeroArray(nums []int, queries [][]int) int {
	vecMp := make([]map[int]int, len(nums))
	for i := range vecMp {
		vecMp[i] = map[int]int{}
		vecMp[i][nums[i]]++
	}
	cnt:=0
	for _,v:=range nums{
		cnt+=v
	}
	if cnt==0{
		return 0
	}
	for j, q := range queries {
		for i := q[0]; i <= q[1]; i++ {

			if _, ok := vecMp[i][q[2]]; ok {
				nums[i] = 0
			}
			newVec := []int{}
			for k, _ := range vecMp[i] {
				if k-q[2] > 0 {
					newVec = append(newVec, k-q[2])
				}
			}
			for _, v := range newVec {
				vecMp[i][v]++
			}
		}
		flag := true
		for _, v := range nums {
			if v != 0 {
				flag = false
			}
		}
		if flag {
			return j + 1
		}
	}
	return -1
}

