class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), sum = 0;
        for (int i = 0; i < n; ++i) {
            sum += nums[i];
        }
        if (sum == 0) return 0;
        vector<unordered_set<int>> subVals(n);
        int m = queries.size();
        for (int i = 0; i < m; ++i) {
            int l = queries[i][0], r = queries[i][1], v = queries[i][2];
            for (int j = l; j <= r; ++j) {
                if (nums[j] == 0) continue;
                if (nums[j] == v) {
                    sum -= nums[j];
                    nums[j] = 0;
                } else if (nums[j] > v) {
                    int target = nums[j] - v;
                    if (subVals[j].count(target)) {
                        sum -= nums[j];
                        nums[j] = 0;
                        subVals[j].clear();
                    } else {
                        unordered_set<int> newSums;
                        newSums.insert(v);
                        for (int s : subVals[j]) {
                            int newSum = s + v;
                            if (newSum <= nums[j]) {
                                newSums.insert(newSum);
                            }
                        }
                        for (int s : newSums) {
                            subVals[j].insert(s);
                        }
                    }
                }
            }
            if (sum == 0) {
                return i + 1;
            }
        }
        return -1;
    }
};

