class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n=nums.size(), m=queries.size();
        vector<vector<int>> values(n, vector<int>(m, 0));
        for(int i=0; i<m; i++){
            int l=queries[i][0], r=queries[i][1], v=queries[i][2];
            for(int j=l; j<=r; j++){
                values[j][i]=v;
            }
        }
        vector finished(n, m+1);
        for(int i=0; i<n; i++){
            vector<int> value=values[i];
            int target=nums[i];
            if(target==0){
                finished[i]=0;
                continue;
            }
            vector<bool> f(target+1, false);
            f[0]=true;
            for(int j=0; j<m; j++){
                for(int k=target; k>=value[j]; k--){
                    f[k]=f[k]||f[k-value[j]];
                }
                if(f[target]){
                    finished[i]=j+1;
                    break;
                }
            }
        }
        if(ranges::max(finished)==m+1){
            return -1;
        }
        else{
            return ranges::max(finished);
        }
    }
};

