class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), m = queries.size();
        // 
        vector<vector<int>> subVal(n);
        for (int i = 0; i < n; ++ i){
            for (int j = 0; j < m; ++ j){
                int l = queries[j][0], r = queries[j][1], v = queries[j][2];
                if (i < l || i > r){
                    subVal[i].emplace_back(0);
                } else {
                    subVal[i].emplace_back(v);
                }
            }
        }        
        int res = -1;
        // 0-1 
        for (int i = 0; i < n; ++ i){
            // 
            vector memo(m, vector<int>(1001, -1));
            auto dfs = [&](this auto&&dfs, int p, int remain) -> int{
                // 
                // 
                if (remain == 0){
                    return 0;
                }
                // 
                if (p == m){
                    return INT_MAX - 1001;
                }
                int &ans = memo[p][remain];
                if (ans != -1){
                    return ans;
                }
                // 
                ans = INT_MAX / 2;
                ans = dfs(p + 1, remain) + 1;
                // 
                if (remain >= subVal[i][p]){
                    ans = min(ans, dfs(p + 1, remain - subVal[i][p]) + 1);
                }
                return ans;
            };
            res = max(res, dfs(0, nums[i]));
        }
        return res >= INT_MAX / 2 ? -1 : res;
    }
};

