                int l = queries[i][0];
                int r = queries[i][1];
                int v = queries[i][2];
                for (int j = l; j <= r; ++j)
                    for (int k = dp[j].size() - 1; k >= 0; --k)
                        if (k >= v)
                            dp[j][k] = max(dp[j][k], dp[j][k - v] + v);
            }
            for (int i = 0; i < dp.size(); ++i) {
                int s = dp[i].size();
                if (dp[i][s - 1] != s - 1)
                    return false;
            }
            return true;
        };
        int l = 0, r = qn, mid;
        while (l <= r) {
            mid = (l + r) / 2;
            check(mid)? (r = mid - 1): (l = mid + 1);
        }
        return (l > qn)? -1: l;
    }
};

