class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        return minK(nums.length, nums, queries);
    }
    public int minK(int n, int[] nums, int[][] queries) {
        int m = queries.length;
        List<List<int[]>> cover = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            cover.add(new ArrayList<>());
        }
        for (int qIdx = 0; qIdx < m; qIdx++) {
            int l = queries[qIdx][0];
            int r = queries[qIdx][1];
            int val = queries[qIdx][2];
            l = Math.max(0, l);
            r = Math.min(n - 1, r);
            for (int i = l; i <= r; i++) {
                cover.get(i).add(new int[]{qIdx, val});
            }
        }

        int maxK = -1;
        for (int i = 0; i < n; i++) {
            int target = nums[i];
            List<int[]> qList = cover.get(i);
            qList.sort((a, b) -> Integer.compare(a[0], b[0]));

            if (target == 0) {
                continue;
            }

            if (qList.isEmpty()) {
                return -1;
            }

            Map<Integer, Integer> dp = new HashMap<>();
            dp.put(0, -1);

            for (int[] query : qList) {
                int idx = query[0];
                int val = query[1];
                Map<Integer, Integer> newDp = new HashMap<>();

                for (Map.Entry<Integer, Integer> entry : dp.entrySet()) {
                    int s = entry.getKey();
                    int currentMax = entry.getValue();

                    if (!newDp.containsKey(s) || currentMax < newDp.get(s)) {
                        newDp.put(s, currentMax);
                    }

                    int newS = s + val;
                    int newMax = Math.max(currentMax, idx);
                    if (!newDp.containsKey(newS) || newMax < newDp.get(newS)) {
                        newDp.put(newS, newMax);
                    }
                }
                for (Map.Entry<Integer, Integer> entry : newDp.entrySet()) {
                    int s = entry.getKey();
                    int newMaxVal = entry.getValue();
                    if (!dp.containsKey(s) || newMaxVal < dp.get(s)) {
                        dp.put(s, newMaxVal);
                    }
                }
            }

            if (!dp.containsKey(target)) {
                return -1;
            }

            int currentK = dp.get(target) + 1;
            if (currentK > maxK) {
                maxK = currentK;
            }
        }

        return maxK != -1 ? maxK : 0;
    }
}

