class Solution {

    public int minZeroArray(int[] nums, int[][] queries) {
        List<Integer>[] lists = new List[nums.length];
        for(int i = 0;i < nums.length;i++){
            lists[i] = new ArrayList<>();
        }
        if(isZeroArray(nums)){
            return 0;
        }
        for(int i = 0;i < queries.length;i++){
            int[] query = queries[i];
            int l = query[0];
            int r = query[1];
            for(int j = l;j <= r;j++){
                lists[j].add(query[2]);
            }
            if(check(nums,lists)){
                return i + 1;
            }
        }
        return -1;
    }

    public boolean isZeroArray(int[] nums){
        for(int num:nums){
            if(num != 0){
                return false;
            }
        }
        return true;
    }

    public boolean check(int[] nums,List<Integer>[] lists){
        for(int i = 0;i < nums.length;i++){
            List<Integer> l = lists[i];
            if(!canSum(l,nums[i])){
                return false;
            }
        }
        return true;
    }

    public boolean canSum(List<Integer> list,int target)
    {
        boolean[] dp = new boolean[target + 1];
        dp[0] = true;
        for(int x : list)
        {
            for(int i = target;i >= x;i--){
                dp[i] = dp[i] || dp[i-x];
            }
        }
        return dp[target];
    }
}

