class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();
        int mx = *max_element(nums.begin(), nums.end());
        if(mx == 0) return 0;

        auto check = [&](int val){
            vector<vector<int>> all(n, vector<int>(11));
            //if could not to all 0, return false;
            for(int i=0;i<val;i++){
                int s = queries[i][0];
                int e = queries[i][1];
                int v = queries[i][2];
                for(int t=s;t<=e;t++){
                    all[t][v]++;
                }
            }

            for(int i=0;i<n;i++){
                if(nums[i] == 0) continue;
                int target = nums[i];
                vector<vector<bool>> f(11, vector<bool>(target+1));
                f[0][0] = true;

                for(int v=1;v<11;v++){
                    for(int t=target;t>=0;t--){
                        for(int c=all[i][v];c>=0;c--){
                            if(v*c<=t) f[v][t] = f[v][t] | f[v-1][t-v*c];
                        }
                    }
                }
                if(!f[10][target]) return false;
            }
            return true;
        };


        int l = 0, r = m;
        while(l < r){
            int c = l + (r - l + 1) / 2;
            if(!check(c)) l = c; 
            else r = c - 1;
        }
        return r == m ? -1 : r + 1;
    }
};

