class Solution {
public:
    int minZeroArray(vector<int>& a, vector<vector<int>>& que) {
        int n = a.size(), m = que.size();

        vector<int> vec[n];
        for ( int i = 0; i < m; i++ ){
            auto& q = que[i];
            int l = q[0], r = q[1], val = q[2];
            for ( int j = 0; j < n; j++ ){
                if ( j >= l && j <= r ) vec[j].push_back(val);
                else vec[j].push_back(0);
            }
        }

        int ans = 0;
        for ( int i = 0; i < n; i++ ){
            if ( a[i] == 0 ) continue;
            vector<int> f(a[i] + 1, 0);
            f[0] = 1;
            for ( int j = 0; j < m; j++ ){
                int val = vec[i][j];
                for ( int k = a[i]; k >= val; k-- ){
                    f[k] |= f[k - val];
                }
                if ( f[a[i]] ){
                    ans = max(ans, j + 1);
                    break;
                }
            }
            if ( !f[a[i]] ) return -1;
        }

        return ans;
    }
};

