class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        vector<int> idx;
        vector<unordered_set<int>> val(n);
        for (int i = 0; i < n; i++) {
            if (nums[i] != 0) {
                val[i].insert(nums[i]);
                idx.push_back(i);
            }
        }
        if (idx.empty()) {
            return 0;
        }

        for (int k = 0; k < queries.size(); k++) {
            auto &q = queries[k];
            for (auto it = idx.begin(); it != idx.end();) {
                int i = *it;
                if (q[0] <= i && i <= q[1]) {
                    vector<int> newN;
                    bool zero = false;
                    for (int num : val[i]) {
                        if (num == q[2]) {
                            zero = true;
                            break;
                        }
                        if (num >= q[2] && val[i].find(num - q[2]) == val[i].end()) {
                            newN.push_back(num - q[2]);
                        }
                    }

                    if (zero) {
                        it = idx.erase(it);
                    } else {
                        for (int num : newN) {
                            val[i].insert(num);
                        }
                        it++;
                    }
                } else {
                    it++;
                }
            }

            if (idx.empty()) {
                return k + 1;
            }
        }
        return -1;
    }
};

