class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:

        @cache
        def dfs(i, tar, temp):
            if tar == 0:
                return i-1
            if i==len(temp):
                return inf
            if tar < 0:
                return inf
            return min(dfs(i+1,tar-temp[i][0], temp), dfs(i+1,tar, temp))


        n = len(nums)

        de = defaultdict(list)
        for index,(i,j,v) in enumerate(queries):
