class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)

        def func(k: int) -> bool:
            mat = [[] for _ in range(n)]
            for i in range(k):
                l, r, v = queries[i]
                for j in range(l, r + 1):
                    if v <= nums[j]:
                        mat[j].append(v)

            @cache
            def dfs(idx: int, x: int) -> bool:
                if idx == lth or arr[idx] > x:
                    return False
                if arr[idx] == x:
                    return True

                return dfs(idx + 1, x - arr[idx]) or dfs(idx + 1, x)

            for i, arr in enumerate(mat):
                arr.sort()
                lth = len(arr)

                if nums[i] > 0 and not dfs(0, nums[i]):
                    return False

                dfs.cache_clear()

            return True

        m = len(queries)
        ans = bisect_left(range(m + 1), True, key=func)

        return -1 if ans == m + 1 else ans

