class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        auto check = [&](int x){
            int n = nums.size();
            vector<vector<int> > cf(n),del(n+1);
            for(int i = 0;i<=x;i++){
                int l = queries[i][0],r = queries[i][1],val = queries[i][2];
                cf[l].push_back(val),del[r+1].push_back(val);
            }
            multiset<int> s;
            for(int i = 0;i<n;i++){
                if(del[i].size()){
                    for(auto u : del[i]){
                        auto res = s.find(u);
                        if(res!=s.end())s.erase(res);
                    }
                }
                if(cf[i].size()){
                    for(auto u : cf[i]){
                        s.insert(u);
                    }
                }
                vector<bool> st(nums[i]+1,false);
                st[0] = true;
                for(auto & u : s){
                    for(int j = nums[i];j>=u;j--){
                        st[j] = st[j]|| st[j-u];
                    }
                }
                if(!st[nums[i]]){
                    return false;
                }
            }
            return true;
        };
        int n = queries.size()-1;
        int l = 0,r = n+1;
        bool flag = true;
        for(int i = 0;i<nums.size();i++){
            if(nums[i] != 0)flag = false;
        }
        if(flag)return 0;

        while(l<r){
            int mid = (l+r)/2;
            if(check(mid))r = mid;
            else l = mid+1;
        }
        if(l == n+1)return -1;
        return l+1;
    }
};

