class Solution {
public:
    vector<int> vis;
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(),m = queries.size();
        vector<vector<pair<int, int>>> grid(n);
        for (int j = 0; j < m; ++j) 
        {
            int l = queries[j][0];
            int r = queries[j][1];
            int val = queries[j][2];
            for (int i = l; i <= r; ++i) grid[i].push_back({val, j});
        }
        for (int i = 0; i < n; ++i) 
        {
            int target = nums[i];
            /*if(target==0)
            {
            }*/

            if (target < 0) return -1;
            else if (target > 0) 
            {
                //meset(dp.size(),INT_MAX);
                vector<int> dp(target + 1, INT_MAX);
                dp[0] = -1;
                for (const auto& p : grid[i]) 
                {
                    int val = p.first;
                    int idx = p.second;
                    vector<int> tmp = dp; 
                    for (int s = 0; s <= target; ++s) 
                    {
                    if (tmp[s] == INT_MAX) continue;
                    int S = s + val;
                    if (S > target) continue;
                    int new_max = max(tmp[s], idx);
                    if (new_max < dp[S]) dp[S] = new_max;
                }
            }

            if (dp[target] == INT_MAX) return -1;
            vis.push_back(dp[target]);
            }  
        }
        bool flag = true;
        for (int num : nums) 
        {
            if (num != 0) 
            {
                flag= false;
                break;
            }
        }
        if (flag||vis.empty())  return 0;
        int res = *max_element(vis.begin(), vis.end());
        return res + 1;
    }
};

