class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int qNum = queries.size();
        int minK = -1;
        int n = nums.size();
        vector<bool> allTure(n,false);
        for(int i = 0;i<n;i++){
            bool dp[nums[i]+1][qNum+1];
            for(int j = 0;j<=qNum;j++){
                dp[0][j]=true;
            }
            for(int j = 1;j<=nums[i];j++){
                dp[j][0]=false;
            }
            for(int j = 1;j<=nums[i];j++){
                for(int k = 1;k<=qNum;k++){
                    if(queries[k-1][0]<=i&&queries[k-1][1]>=i&&j-queries[k-1][2]>=0)
                        dp[j][k]=dp[j-queries[k-1][2]][k-1]||dp[j][k-1];
                    else
                        dp[j][k]=dp[j][k-1];
                }
            }
            for(int j = 0;j<=qNum;j++){
                if(dp[nums[i]][j]){
                    allTure[i]=true;
                    minK = max(minK,j);
                    break;
                }
            }
        }
        for(int i = 0;i<n;i++){
            if(!allTure[i]){
                return -1;
            }
        }
        return minK;
    }
};

