class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
    int q = queries.size();

    // jQj
    vector<tuple<int, string, int>> events;
    for (int i = 0; i < q; ++i) {
        int l = queries[i][0], r = queries[i][1], val = queries[i][2];
        events.emplace_back(l, "add", i);
        events.emplace_back(r + 1, "remove", i);
    }

    // removeadd
    sort(events.begin(), events.end(), [](const auto& a, const auto& b) {
        if (get<0>(a) == get<0>(b)) {
            return get<1>(a) == "remove" && get<1>(b) == "add";
        }
        return get<0>(a) < get<0>(b);
    });

    set<int> current_queries;
    int event_ptr = 0;
    vector<vector<pair<int, int>>> Qj(n);

    for (int j = 0; j < n; ++j) {
        // position <= j
        while (event_ptr < events.size() && get<0>(events[event_ptr]) <= j) {
            int pos = get<0>(events[event_ptr]);
            string typ = get<1>(events[event_ptr]);
            int i = get<2>(events[event_ptr]);

            if (typ == "add") {
                current_queries.insert(i);
            } else {
                if (current_queries.find(i) != current_queries.end()) {
                    current_queries.erase(i);
                }
            }
            ++event_ptr;
        }

        // 
        for (int idx : current_queries) {
            int l = queries[idx][0], r = queries[idx][1], val = queries[idx][2];
            if (l <= j && j <= r) {
                Qj[j].emplace_back(idx, val);
            }
        }
    }

    int max_mj = -1;
    for (int j = 0; j < n; ++j) {
        int target = nums[j];
        if (target == 0) {
            continue;
        }

        vector<pair<int, int>>& queries_j = Qj[j];
        if (queries_j.empty()) {
            return -1;
        }

        unordered_map<int, int> dp;
        dp[0] = -1;
        int min_max_idx = INT_MAX;

        for (auto& [i, val] : queries_j) {
            unordered_map<int, int> tmp;
            for (auto& [s, current_max] : dp) {
                int new_s = s + val;
                int new_max = max(current_max, i);

                if (new_s == target) {
                    if (new_max < min_max_idx) {
                        min_max_idx = new_max;
                    }
                }

                if (tmp.find(new_s) == tmp.end() || new_max < tmp[new_s]) {
                    tmp[new_s] = new_max;
                }
            }

            // tmpdp
            for (auto& [s, new_max] : tmp) {
                if (dp.find(s) == dp.end() || new_max < dp[s]) {
                    dp[s] = new_max;
                }
            }

            if (min_max_idx != INT_MAX) {
