class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        m = len(queries)

        # 
        element_queries = [[] for _ in range(n)]
        for query_idx in range(m):
            l, r, val = queries[query_idx]
            for i in range(l, r + 1):
                if i >= 0 and i < n:
                    element_queries[i].append((query_idx, val))

        k_list = []
        for i in range(n):
            num = nums[i]
            if num == 0:
                k_list.append(0)
                continue

            q_list = element_queries[i]
            target = num
            dp = {0: -1}  # sum: max_index
            found = False
            current_min_k = None

            for (query_idx, val) in q_list:
                new_dp = {}
                # 
                for s in dp:
                    existing_max = dp[s]
                    if s not in new_dp or existing_max < new_dp[s]:
                        new_dp[s] = existing_max
                # 
                for s in list(dp.keys()):
                    s_new = s + val
                    new_max = max(dp[s], query_idx)
                    if s_new in new_dp:
                        if new_max < new_dp[s_new]:
                            new_dp[s_new] = new_max
                    else:
                        new_dp[s_new] = new_max
                dp = new_dp
                # sum
                if target in dp:
                    current_max = dp[target]
                    current_min_k = current_max + 1
                    found = True
                    break  # k_i

            if found:
                k_list.append(current_min_k)
            else:
                return -1

        if not k_list:
            return 0  # 0
        return max(k_list)

