class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        l = len(nums)
        mask = [1] * l
        done = [num == 0 for num in nums]
        target = l - sum(done)
        k = 0
        if target == 0:
            return 0
        for l, r, v in queries:
            k += 1
            for i in range(l, r + 1):
                if done[i]:
                    continue
                mask[i] |= mask[i] << v
                if (mask[i] >> nums[i]) & 1:
                    done[i] = True
                    target -= 1
                    if target == 0:
                        return k
        return -1

