class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        events = []
        for idx, (l, r, val) in enumerate(queries):
            events.append((l, 'add', idx, val))
            events.append((r + 1, 'remove', idx, val))

        events.sort(key=lambda x: (x[0], 0 if x[1] == 'remove' else 1))

        current_queries = []
        element_queries = [[] for _ in range(n)]
        event_ptr = 0

        for i in range(n):
            while event_ptr < len(events) and events[event_ptr][0] == i:
                typ = events[event_ptr][1]
                q_idx = events[event_ptr][2]
                val = events[event_ptr][3]
                if typ == 'add':
                    insert_pos = bisect.bisect_left(current_queries, (q_idx, val))
                    current_queries.insert(insert_pos, (q_idx, val))
                else:
                    pos = bisect.bisect_left(current_queries, (q_idx, val))
                    if pos < len(current_queries) and current_queries[pos] == (q_idx, val):
                        current_queries.pop(pos)
                event_ptr += 1
            element_queries[i] = [ (idx, val) for (idx, val) in current_queries ]

        max_k = -1
        for i in range(n):
            target = nums[i]
            if target == 0:
                continue

            q_list = element_queries[i]
            if not q_list:
                return -1

            dp = {0: -1}
            for (idx, val) in q_list:
                new_dp = {}
                for s in dp:
                    current_max = dp[s]
                    # 
                    if s not in new_dp or current_max < new_dp[s]:
                        new_dp[s] = current_max
                    # 
                    s_new = s + val
                    new_max = max(current_max, idx)
                    if s_new not in new_dp or new_max < new_dp.get(s_new, float('inf')):
                        new_dp[s_new] = new_max
                for s in new_dp:
                    if s not in dp or new_dp[s] < dp.get(s, float('inf')):
                        dp[s] = new_dp[s]

            if target not in dp:
                return -1
            max_k = max(max_k, dp[target])

        if max_k == -1:
            return 0 if all(x == 0 for x in nums) else -1
        else:
            return max_k + 1

