class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();

        int add = 0;
        for (int i = 0; i < n; i++) {
            add += nums[i];
        }
        if (add == 0) return 0;

        unordered_map<int, vector<pair<int, int>>> mp;
        for (int i = 0; i < queries.size(); i++) {
            int l = queries[i][0], r = queries[i][1], val = queries[i][2];
            for (int j = l; j <= r; j++) {
                mp[j].emplace_back(i + 1, val);
            }
        }

        int ans = -1;
        for (int i = 0; i < n; i++) {
            vector<pair<int, int>>& cur = mp[i];
            int x = nums[i], m = cur.size();
            if (x == 0) continue;

            vector<vector<int>> memo(x + 1, vector<int>(m, -1));
            auto dfs = [&](this auto&& dfs, int num, int k) -> int {
                if (num == 0) return k - 1;
                if (num < 0 || k == m) return INT_MAX / 2;
                int& res = memo[num][k]; 
                if (res != -1) return res;
                // 
                res = dfs(num - cur[k].second, k + 1);

                // 
                res = min(res, dfs(num, k + 1));
                return res;
            };
            int a = dfs(x, 0);
            if (a == INT_MAX / 2) return -1;
            ans = max(cur[a].first, ans);
        }
        return ans;
    }
};

