class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        pre = [[] for _ in range(n)]
        for i, [l, r, v] in enumerate(queries):
            for j in range(l, r + 1):
                pre[j].append([v, i+1])

        def cal(goal, array):
            if goal == 0:
                return 0
            s = set()
            s.add(goal)
            for j, l in array:
                for k in s.copy():
                    if k < j:
                        continue
                    elif k > j:
                        s.add(k - j)
                    else:
                        return l
            return -1

        res = -2
        for i in range(n):
            t = cal(nums[i], pre[i])
            res = max(res, cal(nums[i], pre[i])) if t >= 0 else -1
            if res == -1:
                return res
        return res

