class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        // numsqueryvaluequery
        List<List<Pair<Integer, Integer>>> optionListList = new ArrayList<>();
        for (int i = 0; i < nums.length; i++) {
            List<Pair<Integer, Integer>> optionList = new ArrayList<>();
            for (int j = 0; j < queries.length; j++) {
                int[] query = queries[j];
                if (query[0] <= i && query[1] >= i) {
                    optionList.add(new Pair<>(query[2], j));
                }
            }
            optionListList.add(optionList);
        }


        // num0indexindex
        int maxK = 0;
        for (int i = 0; i < nums.length; i++) {
            int target = nums[i];
            List<Pair<Integer, Integer>> options = optionListList.get(i);
            if (target == 0) {
                continue;
            }

            // 0
            int sum = options.stream().mapToInt(Pair::getKey).sum();
            if (sum < target) {
                return -1;
            }

            // koptiontarget0
            int[] dp = new int[target + 1];
            Arrays.fill(dp, Integer.MAX_VALUE);
            dp[0] = -1; // 

            for (Pair<Integer, Integer> p : options) {
                int val = p.getKey();
                int idx = p.getValue();

                // 
                for (int s = target; s >= 0; s--) {
                    if (dp[s] != Integer.MAX_VALUE) {
                        int newSum = s + val;
                        if (newSum <= target) {
                            int newMaxIdx = Math.max(dp[s], idx);
                            dp[newSum] = Math.min(dp[newSum], newMaxIdx);
                        }
                    }
                }
            }
            // System.out.println("dp[] " +  Arrays.toString(dp));
            if (dp[target] == Integer.MAX_VALUE) return -1;
            maxK = Math.max(maxK, dp[target] + 1); // 
        }
        return maxK;
    }
}

