class Solution {
    // 
    // dp
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = queries.length;
        int ans = 0;
        for (int i = 0; i < nums.length; i++) {
            int num = nums[i];
            if (num == 0) {
                continue;
            }
            boolean[] flag = new boolean[num + 1];
            flag[0] = true;
            int k = 0;
            for (; k < n && !flag[num]; k++) {
                if (queries[k][0] > i || queries[k][1] < i) {
                    continue;
                }
                for (int j = num; j >= queries[k][2]; j--) {
                    flag[j] |= flag[j - queries[k][2]];
                }
            }
            if (flag[num]) {
                ans = Math.max(ans, k);
            } else {
                return -1;
            }
        }
        return ans;
    }
}

