class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        if all(num == 0 for num in nums):
            return 0
        ans, n, q = -1, len(nums), len(queries)                 
        op = [[0]*n for i in range(q)]
        for i, que in enumerate(queries):
            for j in range(que[0], que[1]+1):
                op[i][j] = que[2] 
        for i in range(n):
