func solve(nums []int, queries [][]int) int {
    n := len(nums)
    // 
    f := make([][]bool, n)
    for i := range f {
        f[i] = make([]bool, nums[i] + 1)
        f[i][0] = true
    }
    // 
    for k, qItem := range queries {
        l, r, v := qItem[0], qItem[1], qItem[2]

        // 
        for i := range f {
            if i > r || i < l {
                // 
                // 
                continue
            }
            for j := nums[i] - v; j >= 0; j -- {
                if f[i][j] {
                    f[i][j + v] = true
                }
            }
        }

        flag := true
        for i := range f {
            if !f[i][nums[i]] {
                flag = false
                break
            } 
        }
        if flag {
            return k + 1
        }
    }
    return -1
}

