class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        for (int k = 0; k <= queries.length; k++) {
            if (canTransformToZero(nums, queries, k)) {
                return k;
            }
        }
        return -1;
    }

    private boolean canTransformToZero(int[] nums, int[][] queries, int k) {
        List<List<Integer>> indicesValDict = new ArrayList<>();
        for (int i = 0; i < nums.length; i++) {
            indicesValDict.add(new ArrayList<>());
        }

        for (int i = 0; i < k; i++) {
            int l = queries[i][0], r = queries[i][1], val = queries[i][2];
            for (int j = l; j <= r; j++) {
                indicesValDict.get(j).add(val);
            }
        }

        for (int j = 0; j < nums.length; j++) {
            int target = nums[j];
            if (target == 0) {
                continue;
            }
            List<Integer> usableVals = indicesValDict.get(j);
            if (!subsetSumPossible(usableVals, target)) {
                return false;
            }
        }
        return true;
    }

    private boolean subsetSumPossible(List<Integer> arr, int target) {
        boolean[] dp = new boolean[target + 1];
        dp[0] = true;
        for (int v : arr) {
            for (int x = target; x >= v; x--) {
                if (dp[x - v]) {
                    dp[x] = true;
                }
            }
        }
        return dp[target];
    }
}

