class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        mx = max(nums)
        mask = (1 << (mx + 1)) - 1
        dp = [1] * n
        def valid():
            for i in range(n):
                if (dp[i] >> nums[i]) & 1 == 0:
                    return False
            return True
        if valid(): return 0
        for k, (l, r, v) in enumerate(queries):
            for i in range(l, r + 1):
                dp[i] |= dp[i] << v
                dp[i] &= mask
            if valid():
                return k + 1
        return -1

