class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();
        int res = 0;
        for (int i = 0; i < n; ++i) {
            if (nums[i] == 0)
                continue;
            vector<int> dp(nums[i] + 1, 0);
            dp[0] = 1;
            int index = -1;
            for (int j = 0; j < m; ++j) {
                int l = queries[j][0];
                int r = queries[j][1];
                int v = queries[j][2];
                if (l > i || r < i)
                    continue;
                for (int k = nums[i]; k >= v; --k)
                    dp[k] |= dp[k - v];
                if (dp[nums[i]] == 1) {
                    index = j + 1;
                    break;
                }
            }
            if (index < 0)
                return -1;
            res = max(res, index);
        }
        return res;
    }
};

