class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int mx = ranges::max(nums);
        if (mx == 0) return 0;
        int m = queries.size();
        // vector<vector<int>> a(n);
        vector<int> vis(n, -1);
        vector<vector<int>> f(n, vector<int>(mx + 1, 0));
        for (int i = 0; i < n; ++ i) {
            f[i][0] = 1;
        }
        for (int i = 0; i < m; ++ i) {
            int l = queries[i][0], r = queries[i][1], val = queries[i][2];
