                for (int v = nums[j]; v >= 0; -- v) {
                    if (v + val <= nums[j] && f[j][v] == 1) {
                        f[j][val + v] = 1;
                    }
                }
                if (f[j][nums[j]] == 1) {
                    vis[j] = i;
                }
            }
        }
        int ans = 0;
        for (int i = 0; i < n; ++ i) {
            // cout << i << ' ' << vis[i] << endl;
            if (vis[i] == -1) return -1;
            ans = max(ans, vis[i]);
        }
        return ans + 1;
    }
