class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        int m = queries.length;
        List<List<Integer>> list = new ArrayList<>();
        for(int i = 0;i < n; i++) {
            list.add(new ArrayList<>());
        }
        for(int i = 0;i < m; i++) {
            int l = queries[i][0];
            int r = queries[i][1];
            int val = queries[i][2];
            for(int j = 0; j < n; j++) {
                List<Integer> temp = list.get(j);
                if(l <= j && j <= r) temp.add(val);
                else temp.add(0);
                list.set(j,temp);
            }

        }
        int ans= -1;
        for(int i = 0;i < n; i++) {
            List<Integer> temp = list.get(i);
            int[] dp = new int[nums[i] + 1];
            dp[0] = 1;
            if(dp[nums[i]] == 1) {
                ans = Math.max(ans, 0);
                continue;
            }
            for(int j = 0;j < temp.size(); j++) {
                for(int k = nums[i]; k >= 0; k--) {
                    if(k - temp.get(j) >= 0 && dp[k - temp.get(j)] != 0) {
                        dp[k] = 1;
                    }
                }
                if(dp[nums[i]] != 0) {
                    ans = Math.max(ans, j + 1);
                    break;
                }
            }
            if(dp[nums[i]] == 0) return -1;
        }

        return ans;

    }
}

