class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int ans=0;
        int n=nums.size();
        int m=queries.size();
        for(int i=0;i<11;i++){
            v[i].clear();
        }
        for(int i=0;i<m;i++){
            vector<int>x=queries[i];
            int l=x[0],r=x[1],val=x[2];
            for(int j=l;j<=r;j++){
                v[j].push_back(PI{val,i+1});
            }
        }
        for(int i=0;i<n;i++){
            int x=nums[i];
            if(x==0){
                ans=max(ans,0);continue;
            }
            vector<int>dp(x+2,0);
            int l=v[i].size();
            dp[0]=1;
            for(int j=0;j<l;j++){
                PI w=v[i][j];
                int val=w.first;
                int id=w.second;
                for(int k=x-val;k>=0;k--){
                    if(dp[k]){
                        dp[k+val]=id;
                    }
                }
                if(dp[x]){
                    ans=max(ans,dp[x]);break;
                }
            }
            if(dp[x]==0){
