class Solution {
public:
int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
  int n = nums.size(), m = queries.size(), ans = 0;

  for (int i = 0; i < n; ++i) {
    int x = nums[i];
    if (x == 0) {
      continue;
    }

    vector<int> dp(x + 1, INT_MAX);
    dp[0] = -1;

    for (int j = 0; j < m; ++j) {
      auto& q = queries[j];
      int l = q[0], r = q[1], val = q[2];
      if (i < l || i > r)
        continue;

      for (int v = x; v >= 0; --v) {
        if (dp[v] != INT_MAX) {
          int new_v = v + val;
          if (new_v > x) {
            continue;
          }
          dp[new_v] = min(dp[new_v], max(dp[v], j));
        }
      }
    }

    if (dp[x] == INT_MAX)
      return -1;
    ans = max(ans, dp[x] + 1);
  }

  return ans <= m ? ans : -1;
}
};

