class Solution {
public:
    int cal(int pos, int aim, vector<vector<int>>& queries){
        vector<int> dp(aim + 1, -1);
        dp[0] = 0;
        for(int j = 0; j < queries.size(); j++){
            if(queries[j][0] <= pos && queries[j][1] >= pos){
                vector<int> t;
                for(int i = 0; i <= aim; i++){
                    if(dp[i] != -1 && i + queries[j][2] <= aim){
                        if(dp[i + queries[j][2]] == -1){
                            t.push_back(i + queries[j][2]);
                        }
                    }
                }
                for(auto e : t){
                    dp[e] = j + 1;
                }
            }
        }
        return dp[aim];
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int ans = -1;
        for(int i = 0; i < nums.size(); i++){
            int val = cal(i, nums[i], queries);
            if(val == -1){
                return -1;
            }
            ans = max(ans, val);
        }
        return ans;
    }
};

