class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        if self.isOK(nums):
            return 0
        N, M = len(nums), len(queries)
        ans = -1
        for i in range(N):
            if nums[i] == 0: continue
            index = self.getK(i, nums[i], queries, M)
            if index == -1:
                return -1
            ans = max(ans, index)
        return ans

    def isOK(self, nums):
        for v in nums:
            if v != 0:
                return False
        return True

    def getK(self, i, V, queries, M):
        pre = [False for _ in range(1001)]
        for index in range(M):
            [l, r, v] = queries[index]
            if i >= l and i <= r:
                pre2 = [v for v in pre]
                for j in range(1001):
                    if pre[j] and j + v <= 1000:
                        pre2[j + v] = True
                pre2[v] = True
                if pre2[V]:
                    return index + 1
                pre = pre2
        return -1

