class Solution {
    struct Q {
        int id;
        int v;
        Q(int id, int val): id(id), v(val) {}
    };
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int nq = queries.size();

        vector<vector<Q>> vec(n);

        for(int i = 0; i < nq; i ++ ) {
            int l = queries[i][0];
            int r = queries[i][1];
            int v = queries[i][2];
            for(int j = l; j <= r; j ++ ) {
                vec[j].emplace_back(i, v);
            }
        }

        for(auto& v: vec) {
            sort(v.begin(), v.end(), [](const Q& q1, const Q& q2) {return q1.id < q2.id; });
        }

        vector<int> karr(n, -1);
        for(int i = 0; i < n; i ++ ) {
            int t = nums[i];
            auto& qv = vec[i];
            unordered_map<int, int> f;
            f[0] = -1;
            for(auto& q: qv) {
                int j = q.id;
                int v = q.v;
                vector<pair<int, int>> ff;
                for(const auto& [s, cur]: f) {
                    int ns = s + v;
                    int nmax = max(cur, j);
                    ff.emplace_back(ns, nmax);
                }
                for(auto& [s, cur]: ff) {
                    if(f.find(s) == f.end() || cur < f[s]) {
                        f[s] = cur;
                    }
                }
            }
            if(f.find(t) == f.end()) return -1;
            karr[i] = f[t];
        }
        int maxk = ranges::max(karr);
        return maxk == -1? 0: maxk + 1;
    }
};

