class Solution {
public:
    int check(vector<int>& val_list,int target){
        vector<bool> dp(target+1,false);
        dp[0] = true;
        if(target == 0)return 0;
        for(int i = 0;i<val_list.size();++i){
            for(int p = target;p>=val_list[i];--p){
                dp[p] = dp[p-val_list[i]] || dp[p];
                if(dp[target]){
                    return i+1;
                }
            }
        }
        return -1;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();
        vector<vector<int>> candidates(n);
        for(auto q:queries){
            int l = q[0],r = q[1],val = q[2];
            for(int j = 0;j<n;++j){
                if(j >= l && j <= r){
                    candidates[j].push_back(val);
                }else candidates[j].push_back(0);
            }
        }
        int k = -1;
        for(int i = 0;i<n;++i){
            int kk = check(candidates[i],nums[i]);
            if(kk < 0)return -1;
            else k = max(k,kk);
        }
        return k;
    }
};

