class Solution {
public:
    vector<int> w;
    vector<vector<int>> q;
    vector<vector<int>> g;
    int n, mm;

    int minZeroArray(vector<int>& _w, vector<vector<int>>& _q) {
        w = _w, q = _q;
        n = w.size(), mm = q.size();
        int ok = 1;
        for (auto x : w) if (x) ok = 0;
        if (ok) return 0;

        int l = 0, r = mm;

        g.resize(n);
        while (l < r) {  // 
            int m = l + r >> 1;
            if (m == mm) return -1;
            if (check(m))
                r = m;
            else
                l = m + 1;
        }
        return r == mm ? -1 : r + 1;
    }

    bool check(int m) {
        g.assign(n, vector<int>());  //  g
        for (int i = 0; i <= m; i++) {
            int ll = q[i][0], rr = q[i][1], val = q[i][2];
            for (int j = ll; j <= rr; j++) {  // 
                g[j].emplace_back(val);
            }
        }

        for (int i = 0; i < n; i++) {
            if (!canPartition(g[i], w[i])) {
                return false;
            }
        }
        return true;
    }

    bool canPartition(vector<int>& nums, int target) {
        vector<bool> dp(target + 1, false);
        dp[0] = true;

        for (int num : nums) {
            for (int j = target; j >= num; j--) {
                dp[j] = dp[j] | dp[j - num];
            }
        }
        return dp[target];
    }
};

