class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int max = 0;
        for(int i : nums) {
            max = Math.max(max, i);
        }
        if(max == 0) {
            return 0;
        }
        int l = 0, r = queries.length, m;
        while(l < r) {
            m = l + (r - l) / 2;
            boolean f = func(nums, queries, m);
            if(f) {
                r = m;
            } else {
                l = m + 1;
            }
        }
        return l >= queries.length ? -1 : l + 1;
    }

    private boolean func(int[] nums, int[][] queries, int limit) {
        int N = nums.length;
        List<Integer>[] lists = new List[N];
        for(int i = 0; i < lists.length; i++) {
            lists[i] = new ArrayList<>();
        }
        for(int i = 0; i <= limit; i++) {
            int[] q = queries[i];
            for(int j = q[0]; j <= q[1]; j++) {
                lists[j].add(q[2]);
            }
        }
        for(int i = 0; i < lists.length; i++) {
            Collections.sort(lists[i]);
        }
        boolean flag = true;
        for(int i = 0; i < N; i++) {
            flag = flag && check(nums[i], lists[i]);
        }
        return flag;
    }

    private boolean check(int V, List<Integer> list) {
        int N = list.size();
        boolean[][] dp = new boolean[N + 1][V + 1];
        dp[0][0] = true;
        for(int i = 0; i < N; i++) {
            int t = list.get(i);
            for(int j = 0; j <= V; j++) {
                dp[i + 1][j] = dp[i][j];
                if(j >= t) {
                    dp[i + 1][j] =  dp[i + 1][j] || dp[i][j - t];
                } 
            }
        }
        return dp[N][V];
    }
 }

