class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        def minKToZero(nums, queries):
            # 
            for num in nums:
                if num < 0:
                    return -1

            n = len(nums)
            m = len(queries)

            # 
            cover = [[] for _ in range(n)]
            for j in range(m):
                l, r, val = queries[j]
                if l > r:
                    continue  # 
                # i[l, r]
                for i in range(l, r + 1):
                    if i < n:
                        cover[i].append((j, val))

            max_j_list = []
            for i in range(n):
                if nums[i] == 0:
                    max_j_list.append(-1)
                    continue

                queries_i = cover[i]
                if not queries_i:
                    return -1  # 

                # 
                queries_i.sort()

                # j
                dp = {0: -1}
                for (j, val) in queries_i:
                    new_dp = {}
                    # 
                    for s in dp:
                        current_max = dp[s]
                        if s in new_dp:
                            if current_max < new_dp[s]:
                                new_dp[s] = current_max
                        else:
                            new_dp[s] = current_max
                        # 
                        new_s = s + val
                        new_max = max(current_max, j)
                        if new_s in new_dp:
                            if new_max < new_dp[new_s]:
                                new_dp[new_s] = new_max
                        else:
                            new_dp[new_s] = new_max
                    dp = new_dp

                target = nums[i]
                if target not in dp:
                    return -1
                max_j_list.append(dp[target])

            if not max_j_list:
                return 0  # 0

            max_j = max(max_j_list)
            k = max_j + 1
            if k > m:
                return -1
            return k
        return minKToZero(nums, queries)

