class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& qs) {
        int n = nums.size(), m = qs.size();
        int l = -1, r = m, all = accumulate(nums.begin(), nums.end(), 0);
        if (all == 0) {
            return 0;
        }
        auto check = [&](int idx) -> bool {
            vector dp(n, vector<bool>());
            for (int i = 0; i < n; i++) {
                dp[i].resize(nums[i] + 1, false);
                dp[i][0] = true;
            }

            for (int i = 0; i <= idx; i++) {
                int l = qs[i][0], r = qs[i][1], val = qs[i][2];
                for (int j = l; j <= r; j++) {
                    for (int k = nums[j]; k >= val; k--) {
                        dp[j][k] = (dp[j][k] || dp[j][k - val]);
                    }
                }
            }

            bool ok = true;
            for (int i = 0; i < n; i++)
                ok = (ok && dp[i][nums[i]]);
            return ok;
        };
        while (l + 1 != r) {
            int mid = l + r >> 1;
            if (check(mid))
                r = mid;
            else
                l = mid;
        }
        return (r == m ? -1 : r + 1);
    }
};

