type query struct {
    idx, val int
}

func minZeroArray(nums []int, queries [][]int) int {
    n := len(nums)
    m := len(queries)
    c := make([][]query, n)
    for j := 0; j < m; j++ {
        l, r, val := queries[j][0], queries[j][1], queries[j][2]
        for i := l; i <= r; i++ {
            c[i] = append(c[i], query{j, val})
        }
    }

    maxK := -1
    possible := true

    for i := 0; i < n; i++ {
        target := nums[i]
        if target == 0 {
            continue
        }
        dp := make(map[int]int)
        dp[0] = -1
        for _, q := range c[i] {
            j, val := q.idx, q.val
            newDp := make(map[int]int)
            for s, k := range dp {
                newDp[s] = k
                newS := s + val
                if newS <= target {
                    newK := max(k, j)
                    if e, ok := newDp[newS]; !ok || newK < e {
                        newDp[newS] = newK
                    }
                }
            }
            dp = newDp
        }
        if k, ok := dp[target]; ok {
            if k > maxK {
                maxK = k
            }
        } else {
            possible = false
            break
        }
    }

    if !possible {
        return -1
    }
    return maxK + 1
}

